import React from 'react';

const ImageCard = ({ image }) => {
  return (
    <div className="p-4">
      <div className="bg-gradient-to-r from-slate-300 to-slate-500 rounded-lg shadow-md p-4">
        <div className="relative aspect-w-16 aspect-h-9 overflow-hidden">
          <img
            src={image.urls.regular}
            alt={image.alt_description}
            className="object-cover w-full h-full"
          />
        </div>
        <div className="mt-4">
          <div className="text-gray-800 font-bold">
            <a
              href={`https://www.instagram.com/${image.user.instagram_username}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {image.user.name}
            </a>
          </div>
          <div className="flex items-center mt-2">
            <img
              src="heart-icon.png" // Replace with your heart icon image source
              alt="Heart"
              className="w-6 h-6 mr-2 text-red-500"
            />
            <span className="text-gray-600">
              {image.likes} Likes
            </span>
          </div>
          <div className="text-gray-600 mt-2">
            Uploaded on {new Date(image.created_at).toDateString()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ImageCard;
