import React from 'react';
import Modal from 'react-modal';

const ImageModal = ({ isOpen, closeModal, image }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={closeModal}
      contentLabel="Image Modal"
      className="image-modal" // Add a CSS class for styling the modal
    >
      <div className="modal-content">
        <img src={image.urls.full} alt={image.alt_description} className="w-2/3 mx-auto mt-2" />
        <div className="image-details text-center">
          <h2 className="text-lg">{image.user.name}</h2>
          <p className="text-sm">Likes: {image.likes}</p>
          <a
            href={`https://www.instagram.com/${image.user.instagram_username}`}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 text-sm"
          >
            Instagram Profile
          </a>
          <button onClick={closeModal} className="mt-4 bg-blue-600 px-3 py-1 text-white rounded-lg">
            Close
          </button>
        </div>
      </div>
    </Modal>
  );
};

export default ImageModal;
