import React, { useState } from 'react';
import ImageModal from './ImageModal';

const ImageCard = ({ image }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="bg-gradient-to-r from-slate-300 to-slate-500 rounded-lg shadow-md p-4">
      <img
        src={image.urls.regular}
        alt={image.alt_description}
        className="w-full h-auto rounded-lg"
        onClick={openModal} // Open the modal when the image is clicked
      />
      {/* Rest of the code for displaying user details, likes, and other info */}
      <ImageModal isOpen={modalIsOpen} closeModal={closeModal} image={image} />
    </div>
  );
};

export default ImageCard;
