import React, { useState } from 'react';

const DayNightToggle = () => {
  const [isDayMode, setIsDayMode] = useState(true);

  const toggleDayNight = () => {
    setIsDayMode(!isDayMode);

    // Add logic to set dark/light mode in your app based on 'isDayMode'.
    if (isDayMode) {
      document.documentElement.classList.add('dark');
      // Add more dark mode-specific changes here
    } else {
      document.documentElement.classList.remove('dark');
      // Add more light mode-specific changes here
    }
  };

  return (
    <button
      onClick={toggleDayNight}
      className="day-night-toggle p-2 rounded-full absolute right-4 top-4 cursor-pointer"
    >
      {isDayMode ? (
        <img src="https://png.pngtree.com/png-clipart/20210829/original/pngtree-dark-mode-icon-light-png-clipart-png-image_6661697.jpg" alt="Night Mode" className="w-6 h-6" />
      ) : (
        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTnc1UMnu_iSLAL7m5xAIR8OQqomJ-jVq1J43lvN-I0w0fzMS_dib43KXHwIllk7IO-T8I&usqp=CAU" alt="Day Mode" className="w-6 h-6" />
      )}
    </button>
  );
};

export default DayNightToggle;
