import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import ImageGrid from './components/ImageGrid';
import DarkModeSwitch from './components/DarkModeSwitch';

const App = () => {
  const [images, setImages] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Add code here to set dark/light mode based on the state.
    // You can apply CSS classes accordingly to enable dark mode.
  }, []);

  const searchImages = async (query) => {
    setSearchQuery(query);

    try {
      const response = await axios.get('https://api.unsplash.com/search/photos', {
        params: {
          query,
          per_page: 20,
        },
        headers: {
          Authorization: `Client-ID mxIVjm1sdfahgUXlEdfJgNggR5wR7_z7Jdd5ecEMC5Q`,
        },
      });
      setImages(response.data.results);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <DarkModeSwitch /> {/* Include the DarkModeSwitch component */}
      <div className="bg-gradient-to-t from-maroon-400 to-maroon-600 h-screen flex items-center">
        <div className="container mx-auto px-4">
          <h1 className="text-3xl text-center text-white font-semibold mb-4">Search Image</h1>
          <SearchBar onSearch={searchImages} />
        </div>
      </div>

      <div className="container mx-auto p-4">
        {searchQuery && (
          <div className="text-center text-maroon-600 font-semibold text-lg mb-4">
            The results for {searchQuery}
          </div>
        )}
        <ImageGrid images={images} />
      </div>
    </div>
  );
};

export default App;
